ime) AS booking_date FROM ct_bookings WHERE booking_date_time >= '{$frm_dt}' AND booking_date_time <= '{$to_dt}' GROUP BY date(booking_date_time) ORDER BY date(booking_date_time) DESC LIMIT $offset, $total_records_per_page";    }    if (!empty($_POST['order_id']) && empty($_POST['from_dt']) && !empty($_POST['to_dt'])) {        $order_id = trim($_POST['order_id']);    //$frm_dt = trim($_POST['from_dt']);        $to_dt = trim($_POST['to_dt']);        $sql_order = "SELECT date(booking_date_time) AS booking_date FROM ct_bookings WHERE order_id='{$order_id}'  AND booking_date_time <= '{$to_dt}' GROUP BY date(booking_date_time) ORDER BY date(booking_date_time) DESC LIMIT $offset, $total_records_per_page";    }    if (!empty($_POST['order_id']) && !empty($_POST['from_dt']) && empty($_POST['to_dt'])) {        $order_id = trim($_POST['order_id']);        $frm_dt = trim($_POST['from_dt']);    //$to_dt = trim($_POST['to_dt']);        $sql_order = "SELECT date(booking_date_time) AS booking_date FROM ct_bookings WHERE order_id='{$order_id}' AND booking_date_time >= '{$frm_dt}' GROUP BY date(booking_date_time) ORDER BY date(booking_date_time) DESC LIMIT $offset, $total_records_per_page";    }    if (!empty($_POST['order_id']) && empty($_POST['from_dt']) && empty($_POST['to_dt'])) {        $order_id = trim($_POST['order_id']);        /* $frm_dt = trim($_POST['from_dt']);          $to_dt = trim($_POST['to_dt']); */        $sql_order = "SELECT date(booking_date_time) AS booking_date FROM ct_bookings WHERE order_id='{$order_id}' GROUP BY date(booking_date_time) ORDER BY date(booking_date_time) DESC LIMIT $offset, $total_records_per_page";    }} else {$sql_order = "SELECT date(booking_date_time) AS booking_date FROM ct_bookings GROUP BY date(booking_date_time) ORDER BY date(booking_date_time) DESC LIMIT $offset, $total_records_per_page";}$sql_result = mysqli_query($conn, $sql_order);if (mysqli_num_rows($sql_result) > 0) {while ($row_sql = mysqli_fetch_object(