itle='Pending'><em>"+errorobj_pending+"</em></i>");*/                        jQuery(".ct-booking-status").html("<em>" + "Pending" + "</em>");                    } else if (app_details.booking_status == "C") {                        jQuery(".ct-booking-status").html('<i class="fa fa-check txt-success" title="Confirmed"><em>' + "Confirmed" + '</em></i>');                        jQuery('.myconfirmclass').hide();                        jQuery('.confirm_btn_appt').hide();                        if (app_details.task_started) {                            jQuery('.reject_btn_appt').hide();                            jQuery('.myrejectclass').hide();                        }                        jQuery('.mycompleteclass').show();                    } else if (app_details.booking_status == "R") {                        jQuery('.myrejectclass').hide();                        jQuery(".ct-booking-status").html('<i class="fa fa-ban txt-danger" title="Rejected"><em>' + "Rejected" + '</em></i>');                        jQuery('.reject_btn_appt').hide();                        jQuery('.confirm_btn_appt').hide();                        jQuery(".staff_list").hide();                        jQuery(".assign-app-staff").hide();                        jQuery('.myconfirmclass').hide();                        jQuery('.mycompleteclass').hide();                    } else if (app_details.booking_status == "RS") {                        jQuery(".ct-booking-status").html("<i class='fa fa-pencil-square-o txt-info' title='Rescheduled'><em>" + "Rescheduled" + "</em></i>");                        jQuery('.myconfirmclass').show();                        jQuery('.confirm_btn_appt').show();                        //jQuery('.reject_btn_appt').show();                        //jQuery('.myrejectclass').show();                        jQuery('.reject_btn_appt').hide();                        jQuery('.myrejectclass').hide();                        jQuery('.mycompleteclass').hide();