<?phprequire_once "../../objects/class_connection.php";require_once "../../class_configure.php";// API configrequire_once "../config.php";// API headerrequire_once "../header.php";require_once "../../assets/lib/Sinfini_sms.php";/* * Resend_otp.php * Resend OTP */class Resend_OTP {    public function __construct() {}    public function index() {				header('Content-Type: application/json; charset=utf-8');		header('Cache-Control: no-cache');		        $json = array();        if (!empty($_SERVER['REQUEST_METHOD']) && ($_SERVER['REQUEST_METHOD'] == "POST")) {            if (!empty($_POST['customer_id']) && is_numeric($_POST['customer_id'])) {							/*                $header = new API_Header();                $token = $header->get_auth_request_header_key();                $config = new API_Config();                if ($config->check_valid_api_call(trim($token))) {								*/                    $con = new cleanto_db();                    $conn = $con->connect();                    $user_id = trim($_POST['customer_id']);                    $sql_mobile = "SELECT * FROM ct_users WHERE id='{$user_id}'";                    $res_mobile = mysqli_query($conn, $sql_mobile);                    if (mysqli_num_rows($res_mobile) > 0) {                        $row_mobile = mysqli_fetch_object($res_mobile);                        $mobile = $row_mobile->phone;                    } else {                        $mobile = '';                    }                    // OTP                    $otp = $this->generate_otp(4);                    $message = "Yup-Serve : Your OTP verification code is {$otp}";                    // SMS API                    $sms_api = new Sinfini_sms();                    // send SMS using API                    $sms_api->send($mobile, $message);                    $created_at = date('Y-m-d H:i:s', time());                    $expired_at = $this->getCurrentTime();                    // check that user already exists                    $otp_sql = "SELECT * FROM ct_user_reg_otp WHERE mobile_num='{$mobile}'";                    $result_otp = mysqli_query($conn, $otp_sql);                    // check that row exists in OTP table                    if (mysqli_num_rows($result_otp) > 0) {                        // UPDATE OTP table                        $sql_upd = "UPDATE ct_user_reg_otp SET otp='{$otp}', created_at='{$created_at}', expired_at='{$expired_at}' WHERE mobile_num='{$mobile}'";                        mysqli_query($conn, $sql_upd);                    } else {                        // INSERT into OTP table                        $sql_ins = "INSERT INTO ct_user_reg_otp(mobile_num, otp, verify_status, created_at, expired_at) VALUES ("                                . "'{$mobile}', '{$otp}', '{$created_at}', '{$expired_at}'"                                . ")";                        mysqli_query($conn, $sql_ins);                    }                    $json['success']['customer_details'] = array(                        'customer_id' => $user_id                    );                    $json['success']['message'] = 'OTP sent successfully';				/*                } else {                    $json['error']['message'] = "Not authorized to access the API";                } 				*/				            } else {                $json['error']['message'] = "Parameters are missing";            }        } else {            $json['error']['message'] = "The request type is not allowed";        }        echo json_encode($json);    }    // Function to generate OTP     public function generate_otp($n) {        // all numeric digits         $generator = "1357902468";        // Iterate for n-times and pick a single character         // from generator and append it to $result         $result = "";        for ($i = 1; $i <= $n; $i++) {            $result .= substr($generator, (rand() % (strlen($generator))), 1);        }        // Return result         return $result;    }    // GET 15 minute interval    public function getCurrentTime() {        // 15 Minute        return date("Y-m-d H:i:s", time() + 900);    }}$resend = new Resend_OTP();$resend->index();