<?phprequire_once "../../objects/class_connection.php";require_once "../../class_configure.php";// API configrequire_once "../config.php";// API headerrequire_once "../header.php";require_once "../../assets/lib/Firebase.php";/* * update_task.php * Update Task by Service Boy */class Update_Task {    public function __construct() {            }    public function index() {		header('Content-Type: application/json; charset=utf-8');		header('Cache-Control: no-cache');        $json = array();        if (!empty($_SERVER['REQUEST_METHOD']) && ($_SERVER['REQUEST_METHOD'] == "POST")) {            if (!empty($_POST['staff_id']) && !empty($_POST['order_id'])) {			/*                $header = new API_Header();                $token = $header->get_auth_request_header_key();                $config = new API_Config();                if ($config->check_valid_api_call(trim($token))) {			*/                    $con = new cleanto_db();                    $conn = $con->connect();                    // POST values                    $staff_id = trim($_POST['staff_id']);                    $order_id = trim($_POST['order_id']);                                        if (!empty($_POST['requirement'])) {                        // Requirements                        $requirement = trim($_POST['requirement']);                    } else {                        $requirement = '';                    }                                        // File upload configuration                    $targetDir = "../../extra_requirements/";                    $allowTypes = array('jpg', 'png', 'jpeg', 'gif');                                        // BASE URL                    $this->base_url = isset($_SERVER['HTTPS']) && strtolower($_SERVER['HTTPS']) == 'on' ? 'https' : 'http';                    $this->base_url .= '://' . $_SERVER['HTTP_HOST'] . '/';                    // Message            