 ct_booking_extra_requirement_images (requirement_id, order_id, image, created_at) VALUES ('{$last_insert_id}', '{$order_id}', '{$db_file_path1}', '{$current_time}')";                                mysqli_query($conn, $image_sql1);                            }                        }                    } else if (!empty($_FILES['images1']) && !empty($_FILES['images2'])) {                                                // File upload path                        $file1 = basename($_FILES['images1']['name']);                        $file2 = basename($_FILES['images2']['name']);                                                $targetFilePath1 = $targetDir . $file1;                        $targetFilePath2 = $targetDir . $file2;                                                $db_file_path1 = $this->base_url . 'extra_requirements/' . $file1;                        $db_file_path2 = $this->base_url . 'extra_requirements/' . $file2;                        // Check whether file type is valid                        $fileType1 = pathinfo($targetFilePath1, PATHINFO_EXTENSION);                        $fileType2 = pathinfo($targetFilePath2, PATHINFO_EXTENSION);                        if (in_array($fileType1, $allowTypes)) {                            // Upload file to server                            if (move_uploaded_file($_FILES["images1"]["tmp_name"], $targetFilePath1)) {                                // Image db insert sql                                $image_sql1 = "INSERT INTO ct_booking_extra_requirement_images (requirement_id, order_id, image, created_at) VALUES ('{$last_insert_id}', '{$order_id}', '{$db_file_path1}', '{$current_time}')";                                mysqli_query($conn, $image_sql1);                            }                        }                                                if (in_array($fileType2, $allowTypes)) {                            // Upload file to server                   