                                $image_sql1 = "INSERT INTO ct_booking_extra_requirement_images (requirement_id, order_id, image, created_at) VALUES ('{$last_insert_id}', '{$order_id}', '{$db_file_path1}', '{$current_time}')";                                mysqli_query($conn, $image_sql1);                            }                        }                                                if (in_array($fileType2, $allowTypes)) {                            // Upload file to server                            if (move_uploaded_file($_FILES["images2"]["tmp_name"], $targetFilePath2)) {                                // Image db insert sql                                $image_sql2 = "INSERT INTO ct_booking_extra_requirement_images (requirement_id, order_id, image, created_at) VALUES ('{$last_insert_id}', '{$order_id}', '{$db_file_path2}', '{$current_time}')";                                mysqli_query($conn, $image_sql2);                            }                        }                                                if (in_array($fileType3, $allowTypes)) {                            // Upload file to server                            if (move_uploaded_file($_FILES["images3"]["tmp_name"], $targetFilePath3)) {                                // Image db insert sql                                $image_sql3 = "INSERT INTO ct_booking_extra_requirement_images (requirement_id, order_id, image, created_at) VALUES ('{$last_insert_id}', '{$order_id}', '{$db_file_path3}', '{$current_time}')";                                mysqli_query($conn, $image_sql3);                            }                        }                    }                                        $task_message = "Extra requirements added for order #{$order_id}";                    // INSERT into task booking history                    $sql_ins = "INSERT INTO ct_booking_task_history (order_id, staff_id, customer_id, status, created_at) VALUES ('{$order_id}', '{$staff_id}', '{$order_data->client_id}', '{$task_message}', '{$current_time}')";                    mysqli_query($conn, $sql_ins);                    // Send Notification to the customer                    $firebase = new Firebase();                    $firebase->set_api('AIzaSyDfRQ1UO-peN9EI2_QRe_MOZX67-XI9pz8');                    // Get the customer Device Token                    $sql_token = "SELECT token FROM ct_customer_device_token WHERE customer_id='{$order_data->client_id}'";                    $res_token = mysqli_query($conn, $sql_token);                    if (mysqli_num_rows($res_token)) {                        $row_token = mysqli_fetch_object($res_token);                    }                                        // data array to be sent to firebase server                    $data = array(                        'title' => 'Yupserve',                        'request_id' => $order_id,                        'message' => $message,                        'action' => 'booking',                    );                                        $notif_array = array(                        'title' => 'Yupserve',                        'request_id' => $order_id,                        'message' => $message,                        'action' => 'booking',                        'click_action' => 'FCM_PLUGIN_ACTIVITY'                    );                    $firebase->send_to_single($row_token->token, $data, $notif_array);                    // INSERT into customer notification table                    $sql_ins_notif = "INSERT INTO ct_user_notification (user_id, order_id, text, is_read, created_at) VALUES ('{$order_data->client_id}', '{$order_id}', '{$message}', '0', '{$current_time}')";                    mysqli_query($conn, $sql_ins_notif);                    $json['success']['message'] = 'Extra requirements sent to customer for approval';			/*                } else {             