<?php  class cleanto_frequently_discount{				public $id;		public $discount_typename;		public $d_type;		public $rates;		public $status;		public $label;		public $table_name="ct_frequently_discount";		public $conn;		/*Function for Read All data from table*/		public function readall(){			$query="select * from `".$this->table_name."`";			$result=mysqli_query($this->conn,$query);						return $result;		}		/*Function for Read Only one data matched with Id */		public function readone(){			$query="select * from `".$this->table_name."` where `id`='".$this->id."'";			$result=mysqli_query($this->conn,$query);			$value=mysqli_fetch_array($result);			return $value;		}        public function update_discount_status(){            $query="update `".$this->table_name."` set `status`='".$this->status."' where `id`='".$this->id."'";            $result=mysqli_query($this->conn,$query);            return $result;        }        public function update_discount_freq(){            $query="update `".$this->table_name."` set `d_type`='".$this->d_type."',`rates`='".$this->rates."',`labels`='".$this->label."' where `id`='".$this->id."'";            $result=mysqli_query($this->conn,$query);            return $result;        }				/**/		public function readall_front(){		$query="select * from `".$this->table_name."` where `status` = 'E' order by `id` DESC ";		$result=mysqli_query($this->conn,$query);			return $result;		}		/**/		public function readall_freq_dis_for_display_in_scrollbar(){		$query="select * from `".$this->table_name."` where `status` = 'E' limit 1 ";		$result=mysqli_query($this->conn,$query);			return $result;		}}?>